<?php
function rnr_shortcodes_formatter($content) {
	$blcontact_iconock = join("|",array(
						"time",
						"time-item",
						"text",
						"social",
						"newsletter"
						));

	// opening tag
	$rep = preg_replace("/(<p>)?\[($blcontact_iconock)(\s[^\]]+)?\](<\/p>|<br \/>)?/","[$2$3]",$content);

	// closing tag
	$rep = preg_replace("/(<p>)?\[\/($blcontact_iconock)](<\/p>|<br \/>)/","[/$2]",$rep);

	return $rep;
}
add_filter('the_content', 'rnr_shortcodes_formatter',10,2);
add_filter('widget_text', 'rnr_shortcodes_formatter'); 
add_filter( 'widget_text', 'do_shortcode', 11);


/************************** Time Shortcode ******************************************/
function zi_funcation_time($atts , $content = null){
	ob_start();

	$zi_funcation_time = '<div class="time"><ul>
            		'.do_shortcode($content).'
					</ul></div>';
	$zi_funcation_time .=  ob_get_clean();
    return $zi_funcation_time; 
}
add_shortcode('time', 'zi_funcation_time');

