function zi_funcation_social($atts , $content = null){
	ob_start();
	global $theme_options;
	$fb =  $theme_options['social-facebook'];
	$insta =  $theme_options['social-instagram'];
	$google =  $theme_options['social-google-plus'];
	$map =  $theme_options['google-map'];
	$twi =  $theme_options['social-twitter'];
	$you =  $theme_options['social-youtube'];
	$link =  $theme_options['social-linkedin'];
	
	if($fb || $twi || $you || $link || $insta || $google || $map)
	{
		$zi_funcation_social = '<div class="social">';
		$zi_funcation_social .= '<ul>';

		$zi_funcation_social .= ($fb)?'<li><a href="'.$fb.'" target="_blank"><img class="default-logo" src="data:image/png;base64," alt=""/><img class="retina-logo" height="38" width="38" src="data:image/png;base64," alt=""/></a></li>':'';
		$zi_funcation_social .= ($insta)?'<li><a href="'.$insta.'" target="_blank"><img class="default-logo" src="data:image/png;base64," alt=""/>
		<img class="retina-logo" height="38" width="38" src="data:image/png;base64," alt=""/></a></li>':'';
		$zi_funcation_social .= ($google)?'<li><a href="'.$google.'" target="_blank"><img class="default-logo" src="data:image/png;base64," alt=""/> <img class="retina-logo" height="38" width="38" src="data:image/png;base64," alt=""/></a></li>':'';
		$zi_funcation_social .= ($map)?'<li><a href="'.$map.'" target="_blank"><img class="default-logo" src="data:image/png;base64," alt=""/><img class="retina-logo" height="38" width="38" src="data:image/png;base64," alt=""/></a></li>':'';
		
		$zi_funcation_social .= ($twi)?'<li><a href="'.$twi.'" target="_blank"><img class="default-logo" src="data:image/png;base64," alt=""/><img class="retina-logo" height="38" width="38" src="data:image/png;base64," alt=""/></a></li>':'';

		$zi_funcation_social .= ($you)?'<li><a href="'.$you.'" target="_blank"><img src="'.THEME_URI.'images/youtube.png" alt=""/></a></li>':'';
		
		$zi_funcation_social .= ($link)?'<li><a href="'.$link.'" target="_blank"><img class="default-logo" src="data:image/png;base64," alt=""/><img class="retina-logo" height="38" width="38" src="data:image/png;base64," alt=""/></a></li>':'';
		$zi_funcation_social .= '</ul>';
		$zi_funcation_social .= '</div>';
	}
    return $zi_funcation_social; 
}
add_shortcode('social', 'zi_funcation_social');

// NewsLetter
function zi_funcation_newsletter($atts , $content = null){
	ob_start();
	$zi_funcation_newsletter .= '<div class="form">';
	$zi_funcation_newsletter .= '<input type="email" class="form-control zi-emailaddress" placeholder="E-mailadres"/>';
	$zi_funcation_newsletter .= '<input class="site_url" type="hidden" value ="'.SITE_URL.'"/>';
	$zi_funcation_newsletter .= '<button class="btn-orange zi-newsletter" type="submit">Inschrijven</button>';
	$zi_funcation_newsletter .= '<div class="zi-message"></div>';
	$zi_funcation_newsletter .= '</div>';
	$zi_funcation_newsletter .=  ob_get_clean();
    return $zi_funcation_newsletter; 
}
add_shortcode('newsletter', 'zi_funcation_newsletter');
